import fs from "node:fs";

const remove = (
  platform: "linux" | "osx" | "win",
  outDir: string,
  outFile: string,
): Promise<number> => {
  return new Promise((resolve: any, reject: any) => {
    fs.rm(
      `${outDir}/${outFile}.${platform === "linux" ? "tar.gz" : "zip"}`,
      (error) => {
        if (error) {
          reject(1);
        }
      },
    );
    resolve(0);
  });
};

export { remove };
